import { useEffect, useState } from 'react';

import Axios from "axios";
import './style.css'

function Saldo() {

    const [style,setStyle] = useState("dark");
    const changeStyle = () => {
        console.log("you just clicked");
        if (style !== "light") setStyle("light");
        else setStyle("dark");
    };

    /*const [money, setMoney] = useState();
