          setMoney(response.data);
        });
      }, []);*/
      const [money, setMoney] = useState();

    useEffect(() => {
        // Recupera o número da conta corrente da sessão
        const numeroConta = req.session.contaCorrente;

         // Faz a requisição para obter o saldo
        Axios.get(`http://localhost:3001/getSaldo/${numeroConta}`)
            .then((response) => {
            setMoney(response.data);
            })
            .catch((error) => {
            console.error(error);
            // Trate o erro de forma adequada
            });
    }, []);




    return (
        <div className={style}
            onClick={changeStyle}
        >
           <h1>SALDO</h1>
            {typeof money !== "undefined" && <p>R${money}</p>}
        </div>
    );

}

export default Saldo;
