import './style.css';
import {Formik, Form, Field} from "formik";
import * as yup from "yup";
import Axios from "axios";
import { useState } from 'react';


function Login() {

  const handleClickLogin = (values) => {
    Axios.post("http://localhost:3001/login", {
      contaCorrente: values.contaCorrente,
      senha: values.senha,
    }).then((response) => {
      console.log(response);
    });
  };

  const validationLogin = yup.object().shape({
    contaCorrente: yup
    .string()
    .required("Esse campo é obrigatório"),
    senha: yup
    .string()
    .min(4, "A senha deve ter 4 digitos")
    .required("Esse campo é obrigatório")
  });

  const [money, setMoney] = useState();
  const [style,setStyle] = useState("dark");
    const changeStyle = async () => { 
      await Axios.get(`http://localhost:3001/getSaldo`).then((response) => {
        setMoney(response.data[0].saldo);

      });
        console.log("you just clicked");
        if (style !== "light") setStyle("light");
        else setStyle("dark");
    };



  return (
    <div className='login'>
      <div className="login-box">
        <h1>Login</h1>
        <Formik
         initialValues={{}}
         onSubmit={handleClickLogin}

        >
          <Form className='login-form'>
              <div className='login-form-group'>
                  <Field name = "contaCorrente"
                  className = "form-field"
                  placeholder = "Conta-Corrente"
                  />
              </div>

              <div className='login-form-group'>
                    <Field name = "senha"
                    className = "form-field"
                    placeholder = "Senha"
                    />
              </div>

              <button
                  className='submit-login-button'
                  type='submit'>
              Submit
              </button>
              </Form>
         </Formik>
      </div>
      <div className={style}
              onClick={changeStyle}
          >
             <h1>SALDO</h1>
              {typeof money !== "undefined" && <h3>R${money}</h3>}
          </div>
    </div>
  );
}

export default Login;
