import './style.css';
import {Formik, Form, Field} from "formik";
import Axios from "axios";
import { useState } from 'react';


function Login() {

  const handleClickLogin = (values) => {
    Axios.post("http://localhost:3001/login", {
      contaCorrente: values.contaCorrente,
      senha: values.senha,
    }).then((response) => {
      console.log(response);
    });
  };

  const [money, setMoney] = useState();
  const [style,setStyle] = useState("dark");
    const changeStyle = async () => { 
      await Axios.get(`http://localhost:3001/getSaldo`).then((response) => {
        setMoney(response.data[0].saldo);

      });
        console.log("you just clicked");
        if (style !== "light") setStyle("light");
        else setStyle("dark");
    };

    const handleClickDeposit = (values) =>{
      Axios.post("http://localhost:3001/deposito", {
        valor: values.valor,

      }).then((response) => {
        console.log(response);
      });
    };



  return (
    <div className='login'>
      <div className="login-box">
        <h1>Login</h1>
        <Formik
         initialValues={{}}
         onSubmit={handleClickLogin}
        >
          <Form className='login-form'>
              <div className='login-form-group'>
                  <Field name = "contaCorrente"
                  className = "form-field"
                  placeholder = "Conta-Corrente"
                  />
              </div>

              <div className='login-form-group'>
                    <Field name = "senha"
                    className = "form-field"
                    placeholder = "Senha"
                    />
              </div>

              <button
                  className='submit-login-button'
                  type='submit'>
              Submit
              </button>
              </Form>
         </Formik>
      </div>

          <div className='deposito'>
            <h1>Dep√≥sito</h1>
            <Formik 
            initialValues = {{}}
            onSubmit={handleClickDeposit}
            >
                <Form className='deposito-form'>
                    <div className='deposito-form-group'>
                       <Field name = "valor"
                        className = "form-field"
                        placeholder = "Valor a enviar"
                       />
                    </div>
                    <button
                        className='submit-login-button'
                        type='submit'>
                        Submit
                    </button>
                </Form>
            </Formik>
        </div>
        <div className={style}
              onClick={changeStyle}
          >
             <h1>SALDO</h1>
              {typeof money !== "undefined" && <h3>R${money}</h3>}
          </div>
    </div>
  );
}

export default Login;
