import './style.css';
import React, { useState } from 'react';
import {Formik, Form, Field, ErrorMessage} from "formik";
import * as yup from "yup";
import Axios from "axios";

function Login() {
  const [values, setValues] = useState();

  const handleChangeValues = (value) => {
    setValues((prevValue) => ({
      ...prevValue,
      [value.target.name]: value.target.value,
    }));
  };
  const handleClickButton = (values) => {
    Axios.post("http://localhost:3001/login", {
      contaConrrente: values.contaCorrente,
      senha: values.senha,
    }).then(response =>{
      console.log(response);
    });
  };

  return (
    <div className="login-box">
        <h1>Login</h1>
       <Formik
       initialValues={{}}
       >
          <Form className='login-form'>

            <div className='login-form-group'> 
                <Field name = "conta-corrente" className = "form-field"
                placeholder = "Email"
                />
                <ErrorMessage
                component="Span"
                nome= "conta-corrente"
                className="form-erro"/>

            </div>

          </Form>
       </Formik>
        <div className="input">
          <input
            type="text"
            name="Senha"
            id="Senha"
            label="Senha"
            placeholder= "Senha"//"1234 ou 4321"
            onChange={handleChangeValues}
          />
        </div>
        <button className='submit-login-button' onClick={handleClickButton}>Submit</button>
    </div>
  );
}

export default Login;
